from Crypto.Util.number import *
from secret import flag

p = getPrime(1024)
q = getPrime(1024)
n = p * q
e = 7
padding = []
c = []
for i in range(7):
    padding.append(getRandomNBitInteger(200))
    c.append(pow(bytes_to_long(flag)+padding[i],e,n)) 
print("padding =" , padding)
print("c =" , c)
print("n =" , n)


